DROP TABLE IF EXISTS `videofolder`;
CREATE TABLE `videofolder` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '云视频文件夹的主键',
  `folder` varchar(255) DEFAULT NULL COMMENT '文件夹名称',
  `userid` int(11) DEFAULT NULL COMMENT '创建该文件夹的用户id',
  PRIMARY KEY (`id`),
  KEY `id` (`id`),
  KEY `userid` (`userid`),
  CONSTRAINT `videofolder_ibfk_1` FOREIGN KEY (`userid`) REFERENCES `user` (`id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=12 DEFAULT CHARSET=utf8mb4;

