-- ----------------------------
-- Table structure for `videolist`
-- ----------------------------
DROP TABLE IF EXISTS `videolist`;
CREATE TABLE `videolist` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '云视频表的主键',
  `name` varchar(255) DEFAULT NULL COMMENT '云视频名称',
  `info` varchar(255) DEFAULT NULL COMMENT '云视频介绍',
  `publish_time` datetime DEFAULT NULL COMMENT '云视频上传时间',
  `path` int(11) DEFAULT NULL COMMENT '云视频所属文件夹',
  `user_id` int(11) DEFAULT NULL COMMENT '上传的用户id',
  `smallimage` varchar(255) DEFAULT NULL COMMENT '云视频缩略图',
  PRIMARY KEY (`id`),
  KEY `video_ibfk_1` (`user_id`),
  KEY `path` (`path`),
  CONSTRAINT `videolist_ibfk_1` FOREIGN KEY (`user_id`) REFERENCES `user` (`id`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `videolist_ibfk_3` FOREIGN KEY (`path`) REFERENCES `videofolder` (`id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=35 DEFAULT CHARSET=utf8mb4;

